func TestCreateAndRun(t *testing.T){
        con := NewContext()
        con.chainConfig = GetChainConfig()
        statedb,_ := state.New(common.Hash{}, state.NewDatabase(rawdb.NewMemoryDatabase()))
        evm := vm.NewEVM(con.context, statedb, con.chainConfig, con.config)
	imageCode := ""

        interactCode := ""
        //deploy image

        transaction_img := types.TransactionJson{
                Type:"VvmCreate",
                Nonce:1,
                From:"0x2A0b54D5dc17e0AadC383d2db43B0a0D3E029c4c",
                Balance:"1",
                Input:imageCode,
                To:"0x4aB04F903335378702FDcE7252550cA5e997f411",
        }

        res_img,contract_img,usegas_img,err_img := Run_decode(evm, transaction_img)
        log.Println("image create:",res_img,contract_img,usegas_img,err_img)
  img_deploy:=contract_img

  // deploy interact
        transaction_act := types.TransactionJson{
                Type:"VvmCreate",
                Nonce:2,
                From:"0x2A0b54D5dc17e0AadC383d2db43B0a0D3E029c4c",
                Balance:"1",
                Input:interactCode,
                To:"0x4aB04F903335378702FDcE7252550cA5e997f411",
        }

        res_act,contract_act,usegas_act,err_act := Run_decode(evm, transaction_act)
        log.Println("interact create:",res_act,contract_act,usegas_act,err_act)
  act_delpoy:=contract_act

        // set main contract
  content := "0x3ded33bc000000000000000000000000" + strings.Replace(contract_img, "0x", "",-1)
        transaction_act = types.TransactionJson{
                Type:"VvmCall",
                Nonce:3,
                From:"0x2A0b54D5dc17e0AadC383d2db43B0a0D3E029c4c",
                Balance:"1",
                Input:content,
                To:contract_act,
        }
        res_act,contract_act,usegas_act,err_act = Run_decode(evm, transaction_act)
        log.Println("set main contract address:",res_act,contract_act,usegas_act,err_act)

        // checkBalance
        transaction_act = types.TransactionJson{
                Type:"VvmDCall",
                Nonce:3,
                From:"0x2A0b54D5dc17e0AadC383d2db43B0a0D3E029c4c",
                Balance:"1",
                Input:"0x5f51522600000000000000000000000023471aa344372e9c798996aaf7a6159c1d8e3eac",
                To:act_delpoy,
        }
        res_act,contract_act,usegas_act,err_act = Drill_Run_decode(evm, transaction_act)
        log.Println("check balance test:",res_act,contract_act,usegas_act,err_act)

  // img balanceof
  transaction_img = types.TransactionJson{
    Type:"VvmDCall",
    Nonce:3,
    From:"0x2A0b54D5dc17e0AadC383d2db43B0a0D3E029c4c",
    Balance:"1",
    Input:"0x70a0823100000000000000000000000023471aa344372e9c798996aaf7a6159c1d8e3eac",
    To:img_deploy,
  }

  res_img,contract_img,usegas_img,err_img = Drill_Run_decode(evm, transaction_img)
  log.Println("image BalanceOf test:",res_img,contract_img,usegas_img,err_img)

}
